package javapackage;
public class Variable3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     
		byte b= 127;
		System.out.println(b);// -128 to 127 this is the range of byte
		
		short a = -32768;
		System.out.println(a);// short has range between -32768 to 32767
		
		int c= -2147483648;
		 System.out.println(c); // int has range -2147483648 to 2147483647
		 
		 long d= 21474836489l;
		 System.out.println(d); // long has more range than int
		  
		 //basically byte, short, int and long are used for intiger with different size,
		 // bydefoult int is used in java.
		 
		 
		
	}

}
